var method_send = upper(payload.shippingMethod."type")
---
{
  "WSRequestHeader": {
    "System": {
      "name": vars.headers.name,
      "correlationID": vars.headers."X-CORRELATION-ID",
      "processingServer": ""
    },
    "Property": []
  },
  "WSRequestBody": {
    "Audit": {
      "Canal": vars.headers.name
    },
     "Address": {
      "Address": payload.address.address default "",
      "City_DANE": payload.address.address default "",
      "Department_DANE": payload.address.address default ""
    },
    "Bill_Email": if(method_send == "EMAIL") payload.shippingMethod.value else "",
    "Bill_Phone": if(method_send == "SMS" or method_send == "WHATSAPP") payload.shippingMethod.value else "",
    "Delivery_Type": if(method_send == "EMAIL") "Email" 
                      else if(method_send == "WHATSAPP") "WhatsApp"
                      else method_send,
    "Phone": payload.customer.phone default ""
  }
}

