      "Period": ""
    },
    "Customer": {
      "Document_Number": (payload.filtros.DocumentNumber default ""),
      "Document_Type": (payload.filtros.DocumentType default "")
    },
    "Notify": {
      "Email": 
        if (upper(notificationMethod) == "EMAIL") notificationValue 
