ee:message.ee:set-payload:
%dw 2.0
output application/java
import * from dw::core::Numbers
import * from dw::core::Strings
var byte5= toHex(randomInt(1099511627775))
var byte3= toHex(randomInt(16777215))
var epoch= toHex(now() as Number)
var stringhex= epoch ++ byte5 ++ byte3 ++ "0000000000"
---
{
    hexString: substring(stringhex,0,24)
}
